        require(
            beneficiaries[msg.sender].categorySpent[category] + amount <= 
            beneficiaries[msg.sender].categoryLimits[category],
            "Category limit exceeded"
        );

        beneficiaries[msg.sender].categorySpent[category] += amount;
        _recordTransaction(msg.sender, to, amount, category, "Category-based transfer");
